import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import ProblemEditor from './components/ProblemEditor';
import Login from './pages/Login';
import Register from './pages/Register';
import Logout from './pages/Logout';
import useStore from './store/useStore';

// A simple ProtectedRoute that checks if the user is logged in
const ProtectedRoute = ({ children }) => {
  // Assuming your store has an 'isLoggedIn' flag for auth status
  const isLoggedIn = useStore((state) => state.isLoggedIn);
  return isLoggedIn ? children : <Navigate to="/auth/login" />;
};

function App() {
  const isDarkMode = useStore((state) => state.isDarkMode);

  useEffect(() => {
    document.documentElement.classList.toggle('dark', isDarkMode);
  }, [isDarkMode]);

  return (
    <Router>
      <div className="min-h-screen bg-lightThemeBackground dark:bg-darkThemeBackground flex">
        <div className="flex-1">
          <Header />
          <Routes>
            <Route path="/auth/login" element={<Login />} />
            <Route path="/auth/register" element={<Register />} />
            <Route path="/auth/logout" element={<Logout />} />

            {/* Default route "/" now redirects based on auth */}
            <Route
              path="/"
              element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              }
            />

            <Route
              path="/dashboard"
              element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              }
            />
            <Route
              path="/problem/:problemId"
              element={
                <ProtectedRoute>
                  <ProblemEditor />
                </ProtectedRoute>
              }
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
