import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import ProblemEditor from './components/ProblemEditor';
import Login from './pages/Login';
import Register from './pages/Register';
import Logout from './pages/Logout';
import useStore from './store/useStore';

const ProtectedRoute = ({ children }) => {
  const isLoggedIn = useStore((state) => state.isLoggedIn);
  return isLoggedIn ? children : <Navigate to="/auth/login" />;
};

const AppContent = () => {
  const isDarkMode = useStore((state) => state.isDarkMode);
  const location = useLocation();

  useEffect(() => {
    document.documentElement.classList.toggle('dark', isDarkMode);
  }, [isDarkMode]);

  // Check if the current path is under /auth
  const hideHeader = location.pathname.startsWith('/auth');

  return (
    <div className="min-h-screen bg-lightThemeBackground dark:bg-darkThemeBackground flex">
      <div className="flex-1">
        {!hideHeader && <Header />}
        <Routes>
          <Route path="/auth/login" element={<Login />} />
          <Route path="/auth/register" element={<Register />} />
          <Route path="/auth/logout" element={<Logout />} />
          <Route
            path="/"
            element={
              <ProtectedRoute>
                <ProblemEditor />
              </ProtectedRoute>
            }
          />
          <Route
            path="/problem/:problemId"
            element={
              <ProtectedRoute>
                <ProblemEditor />
              </ProtectedRoute>
            }
          />
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <Dashboard />
              </ProtectedRoute>
            }
          />
        </Routes>
      </div>
    </div>
  );
};

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
